import React, { useState } from 'react';
import Home from './src/components/Home';
import Results from './src/components/Results';
import { getRecommendations } from './services/api';

const App = () => {
    const [recommendations, setRecommendations] = useState([]);

    const handleSearch = async ({ artist, genre, audioFile }) => {
        try {
            const result = await getRecommendations(artist, genre, audioFile);
            setRecommendations(result); // Assuming result is an array of songs
        } catch (error) {
            console.error('Error fetching recommendations:', error);
        }
    };

    return (
        <div className="App">
            <Home onSearch={handleSearch} />
            {recommendations.length > 0 && <Results recommendations={recommendations} />}
        </div>
    );
};

export default App;
